function launch(){

	var UI = function(){

		this.Init3D = function(){
			var container = document.getElementById('dots');
			var renderer = new THREE.CanvasRenderer();
			var scene = new THREE.Scene();
			var camera = new THREE.PerspectiveCamera(75,window.innerWidth/window.innerHeight,1,10000);
			var distance = 2500;
			var geometry = new THREE.Geometry();

			renderer.setSize( window.innerWidth+100 , window.innerHeight+100 );
			container.appendChild( renderer.domElement );
			scene.add(camera);

			for( var i=0; i<50; i++){
				var particle = new THREE.Particle( new THREE.ParticleCanvasMaterial({

					color : 0x8c8039,
					opacity:0.50,
					program: function(context){
						context.beginPath();
						context.arc(0,0,2,0,Math.PI * 2,true);
						context.closePath();
						context.fill();
					}

				}));

				particle.position.x = Math.random() * distance * 2 - distance/2;
				particle.position.y = Math.random() * distance * 2 - distance;
				particle.position.z = Math.random() * distance * 2 - distance*1.5;
				particle.scale.x = particle.scale.y = Math.random() * 10 + 5;

				geometry.vertices.push( new THREE.Vertex(particle.position) )
				scene.add(particle);
			}

			var line = new THREE.Line( geometry, new THREE.LineBasicMaterial({
				color : 0x8c8039,
				opacity:0.25
			}));
			scene.add(line);

			camera.position.z = 1000;
			// camera.position.x = -1000;
			camera.lookAt(scene.position);
			renderer.render( scene, camera);


			function eventMouse(event){
				var mouseX = event.clientX - window.innerWidth/2;
				var mouseY = event.clientY - window.innerHeight/2;
				camera.position.x += (mouseX - camera.position.x) * 0.1;
				camera.position.y += (mouseY - camera.position.y) * 0.1;
				camera.lookAt(scene.position);
				renderer.render( scene, camera);
			};



			document.addEventListener('mousemove', eventMouse,false);

		}


	}

	var ui = new UI();
	ui.Init3D();


	setTimeout(function(){
		$('.start').removeClass('init');
	},3000);


	setTimeout(function(){
	$('.desc p.hello').shuffleLetters({
		"text":"Welcome! This website is a work in progress...",
		"step"	: 4,
    "fps"	: 35
	})
	},200);

	setTimeout(function(){
	$('.desc p.work').shuffleLetters({
		"text": "Visit my GitHub to see some of my projects..",
		"step"	: 4,
        "fps"	: 35
	})
},1400);

	setTimeout(function(){
		$('.desc p.bye').shuffleLetters({
		"text": "Thank you!",
		"step"	: 4,
    "fps"	: 35
	})

},2600);


}
$(window).load(function() {

	if(Modernizr.canvas && Modernizr.csstransforms3d && Modernizr.csstransforms3d && Modernizr.rgba){
		IS_LAUNCHED=true;
		launch();
	}else{
		showAlert("Your browser is too old for this website!");
	}

})
